mapper.select:
select store.seq, picture_location, store.owner_id, store_name, location, tel_number, store.owner_comment, open_time, close_time, how_to_come, parking, wifi,animal, group_customer, playroom, disabled
from store
right join review on store.seq=review.store_seq


        and district = #{param1}


        and category = #{param2}


order By ${sort}
